            <label>Ingredient 2:</label>
            <input onChange={ingredInputTwo} type="text" />
            <br />
            <label>Ingredient 3:</label>
            <input onChange={ingredInputThree} type="text" /> */}
            <br />
            <Button variant="success" onClick={getRecipes}>Get Search</Button>

            <RecipeCard isRecipes={isRecipes} />

            {/* {
                isRecipes.map(recipe => (
                    <div key={recipe.id}>
                        <img src={recipe.image} width="250" />
                        <p>{recipe.title}</p>
                        <button>
                            <Link to={{
                                pathname: `/recipe/${recipe.id}`,
                                recipe: {
                                    id: recipe.id,
                                    title: recipe.title,
                                    image: recipe.image
                                }
                            }}>Get Recipe</Link>
                        </button>
                    </div>
                ))
            } */}

        </SessionContext.Provider>
    )
}
