import Axios from 'axios'
import React, { useState, useEffect } from 'react'

export default (recipe) => {
    const API_KEY = process.env.REACT_APP_SPOON_API_KEY

    const [recipeID, setRecipeId] = useState("")


    useEffect(() => {
        setRecipeId(recipe.location.recipe.id)
        console.log(recipe.location.recipe.id)
        Axios.get(`https://api.spoonacular.com/recipes/${recipeID}/analyzedInstructions?apiKey=${API_KEY}`)
        .then(data => {
            console.log(data)
        })
    }, [recipeID])
    return (
        <div>
        </div>
    )
}
