import React, { useState, useContext, useEffect } from 'react';
import axios from 'axios';

import { Link, Redirect, useParams } from 'react-router-dom';
import SessionContext from '../contexts/SessionContext'
import { toast } from 'react-toastify';

import RecipeCard from '../components/RecipeCard';

import Button from 'react-bootstrap/Button'
import InputGroup from 'react-bootstrap/InputGroup'
import Container from 'react-bootstrap/Container'
import FormControl from 'react-bootstrap/FormControl'

import SearchContext from '../contexts/SearchContext';


export default () => {
    const API_KEY = process.env.REACT_APP_SPOON_API_KEY
    const { isLoggedIn } = useContext(SessionContext)

    // const {searchResult} = useContext(SearchContext)


    const [searchResult, setSearchResult] = useState("")

    const [isRecipes, setRecipes] = useState([])



    const getRecipes = (e) => {
        if (searchResult !== "") {
            axios.get(`https://api.spoonacular.com/recipes/findByIngredients?apiKey=${API_KEY}&ingredients=${searchResult}&number=10`)
                .then(response => {
                    console.log(response)
                    console.log(response.data)
                    setRecipes(response.data)
                })
        } else {
            toast.warn('ðŸ¦„ Please fill in ingredient', {
                position: "top-left",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }
    }

    // useEffect(() => {
    //     getRecipes()
    // }, [])


    if (!isLoggedIn || !localStorage.getItem("token")) {
        return <Redirect to="/" />
    }

    const handleSearch = (e) => {
        setSearchResult(e.target.value)
    }


    return (
        <Container>
            <InputGroup className="mb-3">
                <FormControl
                    placeholder="Input Ingredient"
                    aria-label="Input Ingredient"
                    aria-describedby="basic-addon2"
                />
                <InputGroup.Append>
                    <Button variant="success">Look up recipe</Button>
                </InputGroup.Append>
            </InputGroup>




            <RecipeCard isRecipes={isRecipes} />
            </Container>
    )
}



