import React, { useState, useEffect } from 'react';
import WebContent from '../containers/WebContent'
import axios from 'axios';
import { Link } from 'react-router-dom';


export default () => {
    const API_KEY = process.env.REACT_APP_SPOON_API_KEY
    const [random, setRandom] = useState([])

    useEffect(() => {
        axios.get(`https://api.spoonacular.com/recipes/random?apiKey=${API_KEY}&number=10`)
        .then(response => {
            console.log(response.data.recipes)
            setRandom(response.data.recipes)
        })
    }, [])



    return (
        <>
            <WebContent />
            {
                random.map(randomRecipe => (
                    <div key={randomRecipe.id}>
                        <img src={randomRecipe.image} width="250" />
                        <h3>{randomRecipe.title}</h3>
                        <button>
                            <Link to={{
                                pathname: `/recipe/${randomRecipe.id}`,
                                recipe: {
                                    id: randomRecipe.id,
                                    title: randomRecipe.title,
                                    image: randomRecipe.image
                                }
                            }}>Get Recipe</Link>
                        </button>
                    </div>
                ))
            }
        </>
    )
}
